:root{ 
    --color-primary: #2584ff;
    --color-secondary: #00d9ff;
    --color-accent: #ff3400;
    --color-heading: #1b0760;
    --color-body: #918ca4;
    --color-body-darker: #5c5577;
    --color-border: #ccc;
    --border-radius: 30px;

}


*,
*:after,
*:before{ 
    box-sizing: border-box;
}
/* Tyopography */
html{ 
    /* 10px 7isaba */
    font-size: 62.5%;
    
}
body{ 
    font-family: Inter, Arial, Helvetica, sans-serif;
    font-size: 2rem;
    line-height: 1.5; 
    color: var(--color-body);
    
    
    
}

h1,h2,h3{ 
    color: var(--color-heading);
    margin-bottom: 1rem;
    line-height: 1.1;
}

h1{ 
    font-size: 7rem;
}

h2{ 
    font-size: 4rem;
}

h3{ 
    font-size: 3rem;
    
}

p{ 
    margin-top: 0;
}

@media screen and (min-width: 1024px){
    body{ 
        font-size: 1.8rem;
    }
    h1{ 
        font-size: 8rem;
    }
    h2{ 
        font-size: 4rem;
    }
    h3{ 
        font-size: 2.4rem;
    }
}

/* links */
a{ 
    text-decoration: none;
}
.link-arrow{ 
    color: var(--color-accent);
    text-transform: uppercase;
    font-size: 2rem;
    font-weight: bold;
}

.link-arrow::after{ 
    content: "-->";
    margin-left: 5px;
    transition: margin 0.15s;
}
.link-arrow:hover::after{ 
    margin-left: 10px;
    
}

@media screen and (min-width: 1024px) {
    .link-arrow{ 
        font-size: 1.5rem;
    }
    
}

/* badges */

.badge{ 
    
    border-radius: 20px;
    font-size: 2rem;
    font-weight: 500;
    padding: 0.5rem 2rem;
    white-space: nowrap;
}

.badge--primary{ 
    background: var(--color-primary);
    color: white;

}
.badge--secondary{ 
    background: var(--color-secondary);
    color: white;
}
.badge--small{ 
    font-size: 1.6rem;
}

@media screen and (min-width:1024px){ 
    .badge{ 
        font-size: 1.5rem;
    }
    .badge--small{ 
        font-size: 1.2rem;
    }
}

/* lists */
.list{ 
    list-style: none;
    padding-left: 0;
    
}
.list--inline .list__item{ 
    display: inline-block;
    margin-right: 2rem;
    
}

.list--tick{ 
    list-style-image: url(../images/tick.svg);
    padding-left: 3rem;
    
}
.list--tick .list__item{ 
    padding-left: 0.5rem;
    margin-bottom: 1rem;
}

@media  screen and (min-width:1024px) {

    .list--tick .list__item{ 
        padding-left: 0;
    }

    
}

/* icons */

.icon{ 
    width: 40px;
    height: 40px;
}
.icon--small{ 
    width: 30px;
    height: 30px;
}

.icon--primary{ 
    fill: var(--color-primary);
    
}
.icon--white{ 
    fill: white;
}

.icon-container{ 
    background: #f3f9f8;
    width: 64px;
    height: 64px;
    border-radius: 100%;
    display: inline-flex;
    justify-content: center;
    align-items: center;

}

.icon-container--accent{ 
    background: var(--color-accent);
}

/* buttons */

.btn{ 
    border-radius: 40px;
    border: 0;
    cursor: crosshair;
    font-size: 1.8rem;
    font-weight: 600;
    margin: 1rem 0;
    padding: 2rem 4vw;
    text-align: center;
    text-transform: uppercase;
    white-space: nowrap;
}
.btn .icon{ 
    width: 2rem;
    height: 2rem;
    margin-right: 1rem;
    vertical-align: middle;
}

.btn--primary{ 
    background: var(--color-primary);
    color: white;
}
.btn--primary:hover{ 
    background: #007fd4;
}

.btn--secondary{ 
    background: var(--color-secondary);
    color: white;
}

.btn--secondary:hover{ 
    background: #00c8eb;
}

.btn--accent{ 
    background: var(--color-accent);
    color: white;
}

.btn--accent:hover{ 
    background: #ec3000;
}

.btn--stretched{ 
    padding-left: 6rem;
    padding-right: 6rem;
}

.btn--block{ 
    width: 100%;
    display: inline-block;
}

.btn-outline{ 
    background: white;
    color: var(--color-heading);
    border: 2px solid var(--color-heading);
}

.btn-outline:hover{ 
    background: var(--color-heading);
    color: white;
}


@media screen and (min-width: 1024px) { 
    .btn { 
        font-size: 1.5rem;
    }
}

/* inputs */

.input{ 
    padding: 1.5rem 3.5rem;
    border: 1px solid var(--color-border);
    border-radius: var(--border-radius);
    outline: 0;
    color: var(--color-heading);
    font-size: 2rem;
}

::placeholder{ 
    color: #cdcbd7;
    
}
.input-group{ 
    border: 1px solid var(--color-border);
    border-radius: var(--border-radius);
    display: flex;

}

.input-group .input{ 
    border: 0;
    flex-grow: 1;
    padding: 1.5rem 1.7rem;
    width: 0;

}
.input-group .btn{ 
    margin: 4px;
}

@media screen and (min-width:1024px){ 
    .input{ 
        font-size: 1.5rem;
    }
}

/* Cards */
.card {
    border-radius: 7px;
    box-shadow: 0 0 20px 10px #f3f3f3;
    overflow: hidden;
  }
  
  .card__header,
  .card__body {
    padding: 2rem 3rem;
  }
  
  .card--primary .card__header {
    background: var(--color-primary);
    color: #fff;
  }
  .card--primary .badge--primary {
    background: #126de4;
  }
  .card--secondary .badge--secondary{ 
    background: #06c0e0;
  }
  .card--secondary .card__header {
    background: var(--color-secondary);
    color: #fff;
  }

  /* Plans */
  .plan{ 
    transition: transform .2s ease-out;
  }
  .plan__name {
    color: #fff;
    margin: 0;
    font-weight: 500;
    font-size: 2.4rem;
  }
  
  .plan__price {
    font-size: 6rem;
  }
  
  .plan__billing-cycle {
    font-size: 2.4rem;
    font-weight: 300;
    opacity: 0.8;
    margin-right: 1rem;
  }
  
  .plan__description {
    font-size: 2rem;
    font-weight: 300;
    letter-spacing: 1px;
    display: block;
  }
  
  .plan .list__item {
    margin-bottom: 2rem;
  }
  
  .plan--popular{ 
    transform: scale(1.1);
  }
  .plan--popular .card__header {
    position: relative;
  }
  
  .plan--popular .card__header::before {
    content: url(../images/popular.svg);
    width: 40px;
    display: inline-block;
    position: absolute;
    top: -6px;
    right: 5%;
  }

  .plan:hover{ 
    transform: scale(1.05);
  }
  .plan--popular:hover{ 
    transform: scale(1.15);
  }
  
  @media screen and (min-width: 1024px) {
    .plan__name {
      font-size: 1.4rem;
    }
  
    .plan__price {
      font-size: 5rem;
    }
  
    .plan__billing-cycle {
      font-size: 1.6rem;
    }
  
    .plan__description {
      font-size: 1.7rem;
    }
  }

  /* Media */ 

  .media{ 
    display: flex;
  }
  
  .media__body{ 
    margin: 0 2rem;
    padding: 0 0 4rem 0;
  }
  .media__title{ 
    margin-top: 0 ;
    font-style: normal; 
    
  }
  .media__image{ 
    margin-top: 1rem;
    
  }


  /* quotes */
  
  .quote{ 
    font-size: 3rem;
    font-style: italic;
    color: var(--color-body-darker);
    line-height: 1.3;
  }

  .quote__text::before{ 
    content: open-quote;
  }

  .quote__text::after{ 
    content: close-quote;
  }

 .quote__author{ 
    font-size: 3rem;
    font-weight: 500;
    font-style: normal;
    margin: 0;
 }
 .quote__company{ 
    color: var(--color-heading);
    opacity: .4;
    font-size: 2rem;
    font-style: normal;
 }

 .quote__line{ 
    position: relative;
    bottom: 10px;
 }

 @media screen and (min-width:1024px){ 
    .quote{ 
        font-size: 2rem;
    }
    .quote__author{ 
        font-size: 2.4rem;
    }
    .quote__company{ 
        font-size: 1.6rem;
    }
 }

 /* grids */

 .grid{ 
    display: grid;
 }

 @media screen and (min-width:768px) {
    .grid--1x2{ 
        grid-template-columns: 1fr 1fr;
    }
 }
 @media screen and (min-width:1024px) {
    .grid--1x3{ 
        grid-template-columns: repeat(3, 1fr);
    }
 }

 /* testimonials */

 .testimonial{ 
    padding: 3rem;
 }
 .testimonial__image{ 
    position: relative;
 }
 .testimonial__image > img{ 
    width: 100%;
 }

 .testimonial__image > .icon-container{ 
    position: absolute;
    top: 3rem;
    right: -32px;
 }

 @media screen and (min-width:768px){ 
    .testimonial .quote,
    .testimonial .quote__author{ 
        font-size: 2.4rem;
    }
    .testimonial .quote{ 
        margin-left: 6rem;
    }
 }

 /* callouts */

 .callout{ 
    padding: 4rem;
    border-radius: 5px;
 }

 .callout--primary{ 
    background: var(--color-primary);
    color: white;
 }

 .callout__heading{ 
    color: white;
    margin-top: 0;
    font-size: 3rem;
 }

 .callout .btn{ 
    justify-self: center;
    align-self: center;
 }

 .callout__content{ 
    text-align: center;
 }

 @media screen and (min-width:768px){ 
    .callout .grid--1x2{ 
        grid-template-columns: 1fr auto;
    }
    .callout__content{ 
        text-align: left;
    }

    .callout .btn{ 
        justify-self: start;
        margin: 0 2rem;
    }
 }

 /* collapsibles */

 .collapsible__header{ 
    display: flex;
    justify-content: space-between;
 }

 .collapsible__heading{ 
    margin-top: 0;
    font-size: 3rem;
 }

 .collapsible__line{ 
    transform: rotate(0);
    user-select: none;
    transition: transform 0.3s;
 }

 .collapsible__content{ 
    max-height: 0;
    overflow: hidden;
    opacity: 0;
    transition: all 0.3s;
 }

 .collapsible--expanded .collapsible__line{ 
    transform: rotate(90deg);
    
 }

 .collapsible--expanded .collapsible__content{ 
    max-height: 100vh;
    opacity: 1;

 }

 /* Blocks */
.block {
    --padding-vertical: 6rem;
    padding: var(--padding-vertical) 2rem;
  }
  
  .block__header {
    text-align: center;
    margin-bottom: 4rem;
  }
  
  .block__heading {
    margin-top: 0;
  }
  
  .block--dark {
    background: #000;
    color: #7b858b;
  }
  .block--dark h1, .block--dark h2, .block--dark h3 {
    color: #fff;
  }
  .block--light{ 
    background: gainsboro;
    color: #000;
  }
  
  .block--dark .block__heading {
    color: #fff;
  }
  
  .block--skewed-right {
    padding-bottom: calc(var(--padding-vertical) + 4rem);
    clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 90%);
  }
  
  .block--skewed-left {
    padding-bottom: calc(var(--padding-vertical) + 4rem);
    clip-path: polygon(0% 0%, 100% 0%, 100% 95%, 0% 100%);
    
  }
  
  .container {
    max-width: 1140px;
    margin: 0 auto;
  }

  /* navigation bar */

 .nav{ 
    background: black;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    padding: 0 1rem;
    align-items: center;
 }

 .nav__list{ 
    width: 100%;
    margin: 0;
 }

 .nav__item{ 
    padding: 0.5rem 2rem;
    border-bottom: 1px solid #222;
    
 }
 
 .nav__item > a { 
    color: #d2d0db;
    transition: color .2s;
 }
 .nav__item > a:hover { 
    color: white;
 }

 .icon.icon__nav{ 
    border-radius: 10px;
    filter: invert(100%);
    opacity: 0.5;
    outline: 1px solid #d2d0d3;
    transition: box-shadow .15s;
    cursor: url(https://cur.cursors-4u.net/symbols/sym-1/sym46.cur), auto !important;
 }


 .nav.collapsible--expanded .icon__nav{ 
     box-shadow: 0 0 0 3px #666;
     opacity: 1;
 }

.nav__brandlogo{ 
    transform: translateY(5px);
}
 @media screen and (min-width:768px){ 
    .icon.icon__nav{ 
    display: none;
    
   }
   .nav__list{ 
    display: flex;
    font-size: 1.6rem;
    max-height: 100%;
    opacity: 1;
    width: auto;
   }
   .nav__item{ 
    border: 0;
    
   }
 }


 /* hero */
 .hero{ 
    clip-path: polygon(0% 0%, 100% 0%, 100% 90%, 0% 100%);
 }
 .hero__tagline {
    font-size: 2rem;
    color: #b9c3cf;
    letter-spacing: 1px;
    margin: 2rem 0 5rem;
 }

 .hero__image{ 
    width: 100%;
 }
 @media screen and (min-width:768px){ 
    .hero{ 
        padding-top: 0;
    }
    .hero__content{ 
        text-align: left;
        align-self: center;
    }
 }

 /* domain block */

 .block-domain .input-group{ 
    /* offset offset blur radious color*/
    
    box-shadow: 0 0 30px 20px #e6ebee;
    border: 0;
    margin: 4rem auto;
    max-width: 670px;

 }
 .block-domain__prices{ 
    color: var(--color-heading);
    display: grid;
    grid-template-columns: repeat(2,1fr);
    grid-template-rows: repeat(2,6rem);
    font-size: 2rem;
    font-weight: 600;
    justify-items: center;
    max-width: 700px;
    margin: 0 auto;
 }

 @media screen and (min-width:768px){ 
    .block-domain__prices{ 
        /* important */
        grid-template-columns: repeat(auto-fit,minmax(10rem,1fr));
    }
 }

 /* plans block */

 .block-plans .grid{ 
    gap: 8rem 4rem;
    
 }

 .block-plans .card{ 
    max-width: 500px;
    margin: 0 auto;
 }

/* Features */

.feature {
    gap: 4rem 2rem;
    margin: 4rem 0;
  }
  
  .feature:first-of-type {
    margin-top: 8rem;
  }
  
  .feature__heading {
    margin: 1rem 0;
  }
  
  @media screen and (min-width:768px) {
    .feature:nth-of-type(odd) .feature__content {
      order: 2;
    }
  }
  
 /* Showcase Block */
.block-showcase__image > img {
  width: 100%;
}

@media screen and (min-width: 768px) {
  .block-showcase .grid {
    grid-template-columns: 50% 50%;
  }

  .block-showcase__image {
    justify-self: end;
  }

  .block-showcase__image > img {
    width: auto;
    max-width: 700px;
  }
}

/* footer */
.footer{ 
  background: #232323;
}
.footer a{ 
  color: #777;
  margin-top: 0;
  transition: color .2s;
}
.footer a:hover{ 
  color: white;
}
.footer__section{ 
  padding: 2rem;
  border-bottom: 1px solid #393939;
}
.footer__section .list{ 
  margin: 0;
}
.footer__heading{ 
  text-transform: uppercase;
  font-weight: 600;
  color: white;
}
.footer__brand{ 
  margin-top: 5rem;
  text-align: center;
  
}
.footer__brand img{ 
  /* TODO: u can put width:100%; on all img elements */
  width: 100%;
  max-width: 230px;
}

.footer__copyright{ 
  font-size: 2.1rem;
  color: white;
  opacity: .3;
  font-weight: normal;
}


@media screen and (min-width:768px){ 
  .footer__sections{ 
    grid-template-columns: repeat(auto-fit, minmax(10rem,1fr));
  }
  .footer .collapsible__line{ 
    display: none;
  }
  .footer .collapsible__content{ 
    /* important! for showing the conent w/o clicking on the icon/heading */
    opacity: 1;
    max-height: 100%;
  }
  .footer__brand{ 
    order: -1;
    margin-top: 1rem;
  }
  .footer__copyright{ 
    font-size: 1.5rem;
    margin-top: 0;
  }
  .footer__section{ 
    border: 0;
  }
  .footer__heading{ 
    font-size: 2rem;
  }
}

/* Callouts */

.callout {
  padding: 4rem;
  border-radius: 5px;
}

.callout--primary {
  background: var(--color-primary);
  color: #fff;
}

.callout__heading {
  color: #fff;
  margin-top: 0;
  font-size: 3rem;
}

.callout .btn {
  justify-self: center;
  align-self: center;
}

.callout__content {
  text-align: center;
}

@media screen and (min-width: 768px) {
  .callout .grid--1x2 {
    grid-template-columns: 1fr auto;
  }
  .callout__content {
    text-align: left;
  }
  .callout .btn {
    justify-self: start;
    margin: 0 2rem;
  }
}

.callout-signup{ 
  transform: translateY(1rem);
}

/* cleanup shit */
img{ 
  width: 100%;
}

/* whitespace fix */
html, body {
  
  scroll-behavior: smooth;
  scroll-snap-type: y;
}


@media screen and (max-width: 1000px) {
  html, body {
  overflow-x: hidden;
  }
}
